import { BatchProducerModule, InMemoryDatabase, LocalTaskQueue, LocalTaskWorkerModule, ManualBlockTrigger, NoopBaseLayer, PrivateMempool, BlockProducerModule, VanillaTaskWorkerModules, } from "@proto-kit/sequencer";
// TODO Delete
export class InMemorySequencerModules {
    static with(additionalModules) {
        return {
            Database: InMemoryDatabase,
            Mempool: PrivateMempool,
            LocalTaskWorkerModule: LocalTaskWorkerModule.from({
                ...VanillaTaskWorkerModules.withoutSettlement(),
            }),
            BaseLayer: NoopBaseLayer,
            BatchProducerModule,
            BlockProducerModule,
            BlockTrigger: ManualBlockTrigger,
            TaskQueue: LocalTaskQueue,
            // SettlementModule: SettlementModule,
            ...additionalModules,
        };
    }
}
