import { BlockProducerModule, InMemoryDatabase, LocalTaskWorkerModule, NoopBaseLayer, PrivateMempool, UnprovenProducerModule, VanillaTaskWorkerModules, TimedBlockTrigger } from "@proto-kit/sequencer";
export class SequencerWithQueueModules {
    static with(queue, additionalModules) {
        return {
            Database: InMemoryDatabase,
            Mempool: PrivateMempool,
            BaseLayer: NoopBaseLayer,
            BlockProducerModule: BlockProducerModule,
            UnprovenProducerModule: UnprovenProducerModule,
            BlockTrigger: TimedBlockTrigger,
            TaskQueue: queue,
            ...additionalModules,
        };
    }
    static worker(queue, additionalModules) {
        return {
            LocalTaskWorkerModule: LocalTaskWorkerModule.from(VanillaTaskWorkerModules.withoutSettlement()),
            TaskQueue: queue,
            ...additionalModules,
        };
    }
}
